import React from 'react'

const Header = (props) => {
return(
  <div>
    <h1>{props.course}</h1>
  </div>
)
}

const Part = (props) => {
return(
<div>
  <p>{props.part}</p>
</div>
)
}
const Number = (props) => {
  return(
  <div>
    <p>{props.numberOfExercises}</p>
  </div>
  )
  }

const Content = (props) => {
  return(
    <div>
      <p>{props.parts} {props.numberOfExercises} </p>
    </div>
  )
}
const Total = (props) => {
  return(
    <div>
      <p> Number of Exercises {props.totalNumber}
      </p>
    </div>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  return(
    <div>
      <Header course = {course}/>
      <Content parts={part1} numberOfExercises={exercises1}/>
      <Content parts={part2} numberOfExercises={exercises2}/>
      <Content parts={part3} numberOfExercises={exercises3}/>
      <Total totalNumber = {exercises1+exercises2+exercises3}/>
    </div>
  )
}

export default App
