import React from 'react'

const Header = (props) => {
return(
  <div>
    <h1>{props.course}</h1>
  </div>
)
}

const Content = (props) => {
  return(
    <div>
      <p>{props.parts} <br/> {props.numberOfExercises} </p>
    </div>
  )
}

const Total = (props) => {
  return(
    <div>
      <p>
        {props.totalNumber}
      </p>
    </div>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  // return(
  //   <div>
  //     <Header course = {course}/>
  //     <Content parts={part1}/>
  //     <Total totalNumber = {exercises1}/>
  //   </div>
  // )
  return (
    <div>
      <h1>{course}</h1>
      <p>
        {part1} {exercises1}
      </p>
      <p>
        {part2} {exercises2}
      </p>
      <p>
        {part3} {exercises3}
      </p>
      <p>Number of exercises {exercises1 + exercises2 + exercises3}</p>
    </div>
  )
}

export default App
